INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-09-25 17:29:30', '2017-09-25 09:29:30', '欢迎使用WordPress。这是您的第一篇文章。编辑或删除它，然后开始写作吧！', '世界，您好！', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2017-09-25 17:29:30', '2017-09-25 09:29:30', '', 0, 'http://localhost/brand/?p=1', 0, 'post', '', 1),
(2, 1, '2017-09-25 17:29:30', '2017-09-25 09:29:30', '这是示范页面。页面和博客文章不同，它的位置是固定的，通常会在站点导航栏显示。很多用户都创建一个“关于”页面，向访客介绍自己。例如：\r\n<blockquote>欢迎！我白天是个邮递员，晚上就是个有抱负的演员。这是我的博客。我住在天朝的帝都，有条叫做Jack的狗。</blockquote>\r\n……或这个：\r\n<blockquote>XYZ Doohickey公司成立于1971年，自从建立以来，我们一直向社会贡献着优秀doohickies。我们的公司总部位于天朝魔都，有着超过两千名员工，对魔都政府税收有着巨大贡献。</blockquote>\r\n而您，作为一个WordPress用户，我们建议您访问<a href="http://localhost/brand/wp-admin/">控制板</a>，删除本页面，然后添加您自己的页面。祝您使用愉快！', '单页面3', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2017-09-28 15:21:05', '2017-09-28 07:21:05', '', 0, 'http://localhost/brand/?page_id=2', 0, 'page', '', 0),
(4, 1, '2017-09-25 17:43:13', '2017-09-25 09:43:13', '北京**********************\r\n\r\n联系地址：***********************************************************\r\n邮编：******\r\n联系人：郭小姐\r\n联系电话：*******************************\r\n传真：*******************************\r\n手机： *******************************\r\nEmail：*******************************     QQ：*******************************', '联系我', '', 'publish', 'closed', 'closed', '', '%e8%81%94%e7%b3%bb%e6%88%91%e4%bb%ac', '', '', '2017-09-28 15:21:23', '2017-09-28 07:21:23', '', 0, 'http://localhost/brand/?page_id=4', 0, 'page', '', 0),
(5, 1, '2017-09-25 17:43:04', '2017-09-25 09:43:04', '北京****代理有限公司\r\n\r\n联系地址：***********************************************************\r\n邮编：******\r\n联系人：郭小姐\r\n联系电话：*******************************\r\n传真：*******************************\r\n手机： *******************************\r\nEmail：*******************************     QQ：*******************************', '联系我们', '', 'inherit', 'closed', 'closed', '', '4-revision-v1', '', '', '2017-09-25 17:43:04', '2017-09-25 09:43:04', '', 4, 'http://localhost/brand/2017/09/25/4-revision-v1/', 0, 'revision', '', 0),
